    \textbf{А} & $a = m + n$. \\[0.2cm]
    \textbf{Б} & $a = m$. \\[0.2cm]
    \textbf{В} & $a = m - n$. \\[0.2cm]
    \textbf{Г} & $a = mn$. \\[0.2cm]
    \textbf{Д} & $a = n$. \\
    \end{tabular}
\end{minipage}

